import { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faDroplet } from "@fortawesome/free-solid-svg-icons";

const Blue = () => {
  const [wMana, setWMana] = useState(() => {
    const wManaValue = localStorage.getItem("w-mana");
    return wManaValue ? JSON.parse(wManaValue) : 0;
  });

  const wManaGain = () => {
    setWMana(wMana + 1);
  };

  const wManaLoss = () => {
    setWMana(wMana - 1);
  };

  useEffect(() => {
    localStorage.setItem("w-mana", JSON.stringify(wMana));
  }, [wMana]);
  return (
    <div className="col text-center bg-u">
      <p id="blueValue" className="fw-bold">
        0
      </p>

      {/* <img src={BlueIcon} className="img-fluid" alt="Blue" /> */}
      <FontAwesomeIcon icon={faDroplet} className="txt-u" />
    </div>
  );
};

export default Blue;
