import React, { useState, useEffect } from "react";

const PlayerRow = () => {
  // Retrieve data from localStorage, or use default values
  const storedName = localStorage.getItem("playerName") || "Player";
  const storedDamage = parseInt(localStorage.getItem("playerDamage"), 10) || 0;
  const storedEditState = localStorage.getItem("playerEditState") === "true" || false;

  const [name, setName] = useState(storedName);
  const [editState, setEditState] = useState(storedEditState);
  const [damage, setDamage] = useState(storedDamage);

  // Update localStorage whenever name, damage, or editState changes
  useEffect(() => {
    localStorage.setItem("playerName", name);
    localStorage.setItem("playerDamage", damage);
    localStorage.setItem("playerEditState", editState);
  }, [name, damage, editState]);

  const damageDown = () => {
    if (damage > 0) {
      setDamage(damage - 1);
    } else {
      setDamage(0);
    }
  };

  const damageUp = () => {
    setDamage(damage + 1);
  };

  const editName = () => {
    setEditState(true);
  };

  const changeName = (e) => {
    setName(e.target.value);
  };

  const editNameBlur = () => {
    setEditState(false);
  };

  return (
    <div className="my-1 d-flex flex-row justify-content-between align-items-center text-center border-bottom border-2 border-dark">
      {/* Left Row */}
      {editState ? (
        <input
          type="text"
          className="p-1 ms-1 mb-1"
          value={name}
          onChange={changeName}
          onBlur={editNameBlur}
          autoFocus
        />
      ) : (
        <div className="p-1 ms-1 mb-1" onClick={editName}>
          {name}
        </div>
      )}
      {/* Right Row */}
      <div className="d-flex flex-row me-1 mb-1">
        {/* Minus Sign */}
        <div className="py-1 px-3 bg-danger text-white rounded" onClick={damageDown}>
          -
        </div>
        {/* Damage Total */}
        <div className="p-1 mx-2">{damage}</div>
        {/* Plus Sign */}
        <div className="py-1 px-3 bg-success text-white rounded" onClick={damageUp}>
          +
        </div>
      </div>
    </div>
  );
};

export default PlayerRow;
