import "./App.css";
import { useState } from "react";
import Top from "./components/Top/Top";
import Bottom from "./components/Bottom/Bottom";
import { PlayerMenuProvider } from "./components/Bottom/Players/PlayerMenuContext";

const App = () => {
  const [isPlayerMenuVisible, setPlayerMenuVisibility] = useState(false);

  const togglePlayerMenu = () => {
    setPlayerMenuVisibility(!isPlayerMenuVisible);
  };

  return (
    <PlayerMenuProvider>
      <main className="vw-100 vh-100 overflow-hidden">
        <Top togglePlayerMenu={togglePlayerMenu} />
        <Bottom isPlayerMenuVisible={isPlayerMenuVisible} />
      </main>
    </PlayerMenuProvider>
  );
};

export default App;
